mapper.update:

update comments set 
comment=#{comment},
grade=#{grade}
where num = #{num} and id = #{id}

mapper.select:
    	SELECT c.nickname, c.comment, c.curTime, c.grade, c.num
		FROM comments c
		JOIN store s ON c.id = s.id
		WHERE c.id = #{id}
		ORDER BY num DESC
		LIMIT #{start}, #{listSize}
